package es.xabertum.ajicr_app.Encuentros;import android.os.Bundle;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.LinearSmoothScroller;import androidx.recyclerview.widget.RecyclerView;import es.xabertum.ajicr_app.R;import android.util.DisplayMetrics;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import java.util.ArrayList;import java.util.List;public class FragmentCardViewEncuentro extends Fragment {    RecyclerView recyclerView;    RecyclerViewAdapaterEncuentros recyclerViewAdapaterEncuentros;    List<Encuentro> encuentrosList;    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState ) {        getActivity().setTitle("AJICR - Encuentros");        View view = inflater.inflate(R.layout.recyclerview_cardview, container, false);        encuentrosList = new ArrayList<>();        recyclerView = view.findViewById(R.id.recyclerView);        recyclerView.setLayoutManager(new LinearLayoutManager(getActivity()));        recyclerViewAdapaterEncuentros = new RecyclerViewAdapaterEncuentros(getActivity(), encuentrosList);        recyclerView.setAdapter(recyclerViewAdapaterEncuentros);        encuentrosList.add(                new Encuentro(                        "II Encuentro de Jóvenes Investigadores en Ciencias de las Religiones 2019 (II EJICR)",                        "El éxito y la buena acogida del I Encuentro de Jóvenes Investigadores en Ciencias de las Religiones, de...",                        R.drawable.card_e2,                        2                )        );        encuentrosList.add(                new Encuentro(                        "I Encuentro de Jóvenes Investigadores en Ciencias de las Religiones 2017 (I EJICR)",                        "En nombre del comité organizador del I Encuentro de Jóvenes Investigadores en Ciencias de las Religiones...",                        R.drawable.card_e1,                        1                )        );        return view;    }}