package es.xabertum.ajicr_app;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import java.util.ArrayList;import java.util.List;public class Fragment_jornadas extends Fragment {    RecyclerView recyclerView;    RecyclerViewAdapater recyclerViewAdapater;    List<Jornada> jornadaList;    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState ) {        View view = inflater.inflate(R.layout.fragment_jornadas, null);        jornadaList = new ArrayList<>();        recyclerView = (RecyclerView) view.findViewById(R.id.recyclerView);        recyclerView.setHasFixedSize(true);        recyclerView.setLayoutManager(new LinearLayoutManager(getActivity()));        jornadaList.add(              new Jornada(                      "V Jornadas Universitarias de AJICR: “Política y Religiones”",                      "Religiones y Educación: ¿Por Qué? ¿Cómo?",                      R.drawable.politica_2_cropped,                      1              )        );        jornadaList.add(                new Jornada(                        "IV Jornadas Universitarias de AJICR: “La muerte sale de fiesta”",                        "Con motivo de la tradicional festividad de los difuntos compartida en Occidente, la Asociación decidió...",                        R.drawable.muerte_cropped,                        2                )        );        jornadaList.add(                new Jornada(                        "III Jornadas Universitarias de AJICR: “Arte y Religiones: Simbología y Espacios de Culto”",                        "A la luz de la incuestionable relevancia de los significados de los símbolos y manifestaciones artísticas...",                        R.drawable.card_j3,                        3                )        );        jornadaList.add(                new Jornada(                        "II Jornadas Universitarias de AJICR: “Voces de Religiones y de Políticas en la Esfera Pública”",                        "El día 7 de Junio de 2017 se han celebrado, en el Salón de Actos de la Facultad de Filología de la Universidad Complutense de Madrid...",                        R.drawable.arte_no_cropped,                        4                )        );        jornadaList.add(                new Jornada(                        "I Jornadas Universitarias de AJICR: RELIGIO.NET: Cibertecnología y creencias religiosas.",                        "Religio.Net nace como proyecto emergente de la voluntad de los miembros de la Asociación de Jóvenes Investigadores en Ciencias de las Religiones...",                        R.drawable.arte_no_cropped,                        5                )        );        recyclerViewAdapater = new RecyclerViewAdapater(getActivity(), jornadaList);        recyclerView.setAdapter(recyclerViewAdapater);        return view;    }}